{
  "base_name": "Lead-Qualifizierungs-MVP",
  "tables": {
    "leads": {
      "name": "üéØ Leads",
      "fields": [
        {
          "name": "Lead ID",
          "type": "auto_number",
          "primary": true
        },
        {
          "name": "Name",
          "type": "single_line_text",
          "required": true
        },
        {
          "name": "Unternehmen",
          "type": "single_line_text",
          "required": true
        },
        {
          "name": "E-Mail",
          "type": "email",
          "required": true
        },
        {
          "name": "Telefon",
          "type": "phone_number"
        },
        {
          "name": "Branche",
          "type": "single_select",
          "options": [
            "Immobilien",
            "Unternehmensberatung",
            "Verm√∂gensberatung",
            "IT-Dienstleister",
            "Versicherung",
            "Digitale Agentur",
            "Handwerk",
            "Sonstige"
          ]
        },
        {
          "name": "Firmengr√∂√üe",
          "type": "single_select",
          "options": [
            "1-5 Mitarbeiter",
            "6-20 Mitarbeiter",
            "21-50 Mitarbeiter",
            "51-200 Mitarbeiter",
            "200+ Mitarbeiter"
          ]
        },
        {
          "name": "Jahresumsatz",
          "type": "single_select",
          "options": [
            "< ‚Ç¨100k",
            "‚Ç¨100k - ‚Ç¨500k",
            "‚Ç¨500k - ‚Ç¨1M",
            "‚Ç¨1M - ‚Ç¨5M",
            "‚Ç¨5M+"
          ]
        },
        {
          "name": "Lead-Quelle",
          "type": "single_select",
          "options": [
            "Website-Formular",
            "LinkedIn",
            "XING",
            "Empfehlung",
            "Kaltakquise",
            "Event",
            "CSV-Import",
            "Manual"
          ]
        },
        {
          "name": "Status",
          "type": "single_select",
          "options": [
            "Neu",
            "Kontaktiert",
            "Qualifiziert",
            "Angebot",
            "Verhandlung",
            "Gewonnen",
            "Verloren",
            "Inaktiv"
          ]
        },
        {
          "name": "Lead-Score",
          "type": "formula",
          "formula": "IF(AND({Branche} = 'Immobilien', {Firmengr√∂√üe} = '6-20 Mitarbeiter'), 85, IF(AND({Branche} = 'Unternehmensberatung', {Jahresumsatz} = '‚Ç¨1M - ‚Ç¨5M'), 90, IF({Branche} = 'Verm√∂gensberatung', 80, IF({Firmengr√∂√üe} = '21-50 Mitarbeiter', 70, IF({Jahresumsatz} = '‚Ç¨500k - ‚Ç¨1M', 65, 50)))))"
        },
        {
          "name": "Priorit√§t",
          "type": "formula",
          "formula": "IF({Lead-Score} >= 85, 'üî• Hei√ü', IF({Lead-Score} >= 70, '‚≠ê Warm', IF({Lead-Score} >= 50, 'üìù Kalt', '‚ùÑÔ∏è Inaktiv')))"
        },
        {
          "name": "Potenzial ‚Ç¨/Monat",
          "type": "formula",
          "formula": "IF({Branche} = 'Unternehmensberatung', 2000, IF({Branche} = 'Verm√∂gensberatung', 1000, IF({Branche} = 'Immobilien', 500, IF(OR({Branche} = 'IT-Dienstleister', {Branche} = 'Digitale Agentur'), 300, 200))))"
        },
        {
          "name": "Letzter Kontakt",
          "type": "date"
        },
        {
          "name": "N√§chster Follow-up",
          "type": "date"
        },
        {
          "name": "Notizen",
          "type": "long_text"
        },
        {
          "name": "Tags",
          "type": "multiple_select",
          "options": [
            "Pilotprojekt",
            "Beta-Tester",
            "Premium-Kunde",
            "Referral-Partner",
            "Event-Lead",
            "Demo-Request"
          ]
        },
        {
          "name": "Erstellt am",
          "type": "created_time"
        },
        {
          "name": "Bearbeitet am",
          "type": "last_modified_time"
        }
      ],
      "views": [
        {
          "name": "üî• Hei√üe Leads",
          "type": "grid",
          "filter": "{Lead-Score} >= 85",
          "sort": [{"field": "Lead-Score", "direction": "desc"}]
        },
        {
          "name": "üìÖ Follow-up heute",
          "type": "grid",
          "filter": "{N√§chster Follow-up} = TODAY()",
          "sort": [{"field": "Priorit√§t", "direction": "desc"}]
        },
        {
          "name": "üí∞ Revenue Pipeline",
          "type": "grid",
          "filter": "OR({Status} = 'Qualifiziert', {Status} = 'Angebot', {Status} = 'Verhandlung')",
          "sort": [{"field": "Potenzial ‚Ç¨/Monat", "direction": "desc"}]
        }
      ]
    },
    "follow_up_templates": {
      "name": "üìß Follow-up Templates",
      "fields": [
        {
          "name": "Template Name",
          "type": "single_line_text",
          "primary": true
        },
        {
          "name": "Branche",
          "type": "link_to_another_record",
          "linked_table": "leads",
          "linked_field": "Branche"
        },
        {
          "name": "Sequenz-Schritt",
          "type": "number"
        },
        {
          "name": "Betreff",
          "type": "single_line_text"
        },
        {
          "name": "E-Mail Text",
          "type": "long_text"
        },
        {
          "name": "Follow-up nach (Tage)",
          "type": "number"
        },
        {
          "name": "Aktiv",
          "type": "checkbox",
          "default": true
        }
      ]
    },
    "activities": {
      "name": "üìä Aktivit√§ten",
      "fields": [
        {
          "name": "Aktivit√§t ID",
          "type": "auto_number",
          "primary": true
        },
        {
          "name": "Lead",
          "type": "link_to_another_record",
          "linked_table": "leads"
        },
        {
          "name": "Typ",
          "type": "single_select",
          "options": [
            "E-Mail gesendet",
            "E-Mail erhalten",
            "Anruf get√§tigt",
            "Anruf erhalten",
            "Meeting",
            "Demo",
            "Angebot",
            "Notiz"
          ]
        },
        {
          "name": "Datum",
          "type": "date"
        },
        {
          "name": "Beschreibung",
          "type": "long_text"
        },
        {
          "name": "Erstellt am",
          "type": "created_time"
        }
      ]
    }
  },
  "automations": [
    {
      "name": "Neuer Lead ‚Üí Follow-up planen",
      "trigger": "Record created in Leads",
      "actions": [
        "Update record: Set 'N√§chster Follow-up' to 1 day from now",
        "Send email notification to sales team"
      ]
    },
    {
      "name": "Follow-up √ºberf√§llig ‚Üí Reminder",
      "trigger": "Daily at 9:00 AM",
      "condition": "'N√§chster Follow-up' is before today",
      "actions": [
        "Send email reminder to assigned user"
      ]
    }
  ]
}

{
  "zapier_workflows": {
    "workflow_1": {
      "name": "Lead Import & Initial Scoring",
      "trigger": {
        "app": "Typeform",
        "event": "New Entry",
        "form": "Lead Qualification Form"
      },
      "actions": [
        {
          "step": 1,
          "app": "Airtable",
          "action": "Create Record",
          "table": "Leads",
          "mapping": {
            "Name": "{{Name}}",
            "Unternehmen": "{{Company}}",
            "E-Mail": "{{Email}}",
            "Telefon": "{{Phone}}",
            "Branche": "{{Industry}}",
            "Firmengr√∂√üe": "{{Company_Size}}",
            "Jahresumsatz": "{{Revenue}}",
            "Lead-Quelle": "Website-Formular",
            "Status": "Neu",
            "N√§chster Follow-up": "{{$datetime:add:1:day}}"
          }
        },
        {
          "step": 2,
          "app": "Slack",
          "action": "Send Channel Message",
          "channel": "#leads",
          "message": "üéØ Neuer Lead: {{Name}} von {{Company}} ({{Industry}}) - Score: {{Lead-Score}}"
        },
        {
          "step": 3,
          "app": "Gmail",
          "action": "Send Email",
          "to": "{{Email}}",
          "subject": "Danke f√ºr Ihr Interesse - n√§chste Schritte",
          "body": "Hallo {{Name}},\n\nvielen Dank f√ºr Ihr Interesse an LeadScore Pro!\n\nIch melde mich innerhalb von 24 Stunden bei Ihnen, um Ihre spezifischen Anforderungen zu besprechen.\n\nBis dahin k√∂nnen Sie gerne unsere Case Studies durchsehen:\n[Link zu Case Studies]\n\nBeste Gr√º√üe\nIhr LeadScore Pro Team"
        }
      ]
    },
    "workflow_2": {
      "name": "CSV Lead Import Processing",
      "trigger": {
        "app": "Gmail",
        "event": "New Email",
        "filter": "has attachment AND subject contains 'Lead Import'"
      },
      "actions": [
        {
          "step": 1,
          "app": "Parser by Zapier",
          "action": "Extract Data",
          "template": "CSV_Lead_Template"
        },
        {
          "step": 2,
          "app": "Airtable",
          "action": "Create Multiple Records",
          "table": "Leads",
          "mapping": {
            "Name": "{{Name}}",
            "Unternehmen": "{{Company}}",
            "E-Mail": "{{Email}}",
            "Branche": "{{Industry}}",
            "Lead-Quelle": "CSV-Import",
            "Status": "Neu"
          }
        }
      ]
    },
    "workflow_3": {
      "name": "Daily Follow-up Reminders",
      "trigger": {
        "app": "Schedule by Zapier",
        "event": "Every Day",
        "time": "09:00"
      },
      "actions": [
        {
          "step": 1,
          "app": "Airtable",
          "action": "Find Records",
          "table": "Leads",
          "filter": "AND({N√§chster Follow-up} <= TODAY(), {Status} != 'Gewonnen', {Status} != 'Verloren')"
        },
        {
          "step": 2,
          "app": "Gmail",
          "action": "Send Email",
          "to": "sales@company.com",
          "subject": "üìÖ Follow-up Reminder - {{count}} Leads heute",
          "body": "Guten Morgen!\n\nHeute stehen {{count}} Follow-ups an:\n\n{{#each records}}\n- {{Name}} ({{Company}}) - Score: {{Lead-Score}} - {{Priorit√§t}}\n{{/each}}\n\n[Airtable √∂ffnen]\n\nViel Erfolg!\nIhr Automatisierungs-System"
        }
      ]
    },
    "workflow_4": {
      "name": "High-Score Lead Alert",
      "trigger": {
        "app": "Airtable",
        "event": "Updated Record",
        "table": "Leads",
        "filter": "{Lead-Score} >= 85"
      },
      "actions": [
        {
          "step": 1,
          "app": "Slack",
          "action": "Send Direct Message",
          "user": "@sales-manager",
          "message": "üî• HOT LEAD ALERT!\n\n{{Name}} - {{Company}}\nScore: {{Lead-Score}}/100\nBranche: {{Branche}}\nPotenzial: ‚Ç¨{{Potenzial ‚Ç¨/Monat}}/Monat\n\n[Sofort kontaktieren]"
        },
        {
          "step": 2,
          "app": "Airtable",
          "action": "Update Record",
          "fields": {
            "Tags": "Hot Lead Alert",
            "N√§chster Follow-up": "{{$datetime:now}}"
          }
        }
      ]
    },
    "workflow_5": {
      "name": "Automated Follow-up Email Sequence",
      "trigger": {
        "app": "Airtable",
        "event": "Updated Record",
        "table": "Leads",
        "filter": "AND({Status} = 'Kontaktiert', {Letzter Kontakt} <= DATEADD(TODAY(), -3, 'days'))"
      },
      "actions": [
        {
          "step": 1,
          "app": "Filter by Zapier",
          "condition": "{{Branche}} exists"
        },
        {
          "step": 2,
          "app": "Airtable",
          "action": "Find Record",
          "table": "Follow-up Templates",
          "filter": "{Branche} = '{{Branche}}' AND {Sequenz-Schritt} = 2"
        },
        {
          "step": 3,
          "app": "Gmail",
          "action": "Send Email",
          "to": "{{E-Mail}}",
          "subject": "{{Betreff}}",
          "body": "{{E-Mail Text}}"
        },
        {
          "step": 4,
          "app": "Airtable",
          "action": "Create Record",
          "table": "Activities",
          "mapping": {
            "Lead": "{{Lead ID}}",
            "Typ": "E-Mail gesendet",
            "Datum": "{{$datetime:now}}",
            "Beschreibung": "Automatische Follow-up E-Mail (Sequenz 2) versendet"
          }
        }
      ]
    },
    "workflow_6": {
      "name": "Lead Status Change Notifications",
      "trigger": {
        "app": "Airtable",
        "event": "Updated Record",
        "table": "Leads",
        "filter": "{Status} = 'Gewonnen' OR {Status} = 'Verloren'"
      },
      "actions": [
        {
          "step": 1,
          "app": "Slack",
          "action": "Send Channel Message",
          "channel": "#sales-updates",
          "message": "{{#if Status == 'Gewonnen'}}üéâ DEAL WON!{{else}}‚ùå Deal Lost{{/if}}\n\n{{Name}} - {{Company}}\nWert: ‚Ç¨{{Potenzial ‚Ç¨/Monat}}/Monat\nBranche: {{Branche}}\nDauer: {{days_since_created}} Tage"
        },
        {
          "step": 2,
          "app": "Google Sheets",
          "action": "Create Spreadsheet Row",
          "spreadsheet": "Sales Dashboard",
          "values": {
            "Date": "{{$datetime:now}}",
            "Lead": "{{Name}}",
            "Company": "{{Company}}",
            "Status": "{{Status}}",
            "Value": "{{Potenzial ‚Ç¨/Monat}}",
            "Industry": "{{Branche}}",
            "Source": "{{Lead-Quelle}}"
          }
        }
      ]
    }
  },
  "zapier_tables": {
    "csv_parser_template": {
      "name": "CSV Lead Import Template",
      "columns": [
        "Name",
        "Company",
        "Email",
        "Phone",
        "Industry",
        "Company_Size",
        "Revenue",
        "Source",
        "Notes"
      ],
      "mapping": {
        "Name": "column_1",
        "Company": "column_2",
        "Email": "column_3",
        "Phone": "column_4",
        "Industry": "column_5",
        "Company_Size": "column_6",
        "Revenue": "column_7",
        "Source": "column_8",
        "Notes": "column_9"
      }
    }
  },
  "webhook_endpoints": {
    "lead_import": {
      "url": "https://hooks.zapier.com/hooks/catch/12345/abcdef/",
      "method": "POST",
      "description": "Manual lead import via API",
      "payload": {
        "name": "string",
        "company": "string",
        "email": "string",
        "phone": "string",
        "industry": "string",
        "source": "string"
      }
    },
    "scoring_update": {
      "url": "https://hooks.zapier.com/hooks/catch/12345/ghijkl/",
      "method": "POST",
      "description": "Update lead scoring",
      "payload": {
        "lead_id": "string",
        "new_score": "number",
        "reason": "string"
      }
    }
  }
}

{
  "dashboard_name": "LeadScore Pro Dashboard",
  "data_sources": [
    {
      "name": "Airtable_Leads",
      "type": "Airtable",
      "base_id": "{{AIRTABLE_BASE_ID}}",
      "table": "Leads",
      "api_key": "{{AIRTABLE_API_KEY}}"
    },
    {
      "name": "Airtable_Activities",
      "type": "Airtable", 
      "base_id": "{{AIRTABLE_BASE_ID}}",
      "table": "Activities",
      "api_key": "{{AIRTABLE_API_KEY}}"
    }
  ],
  "pages": [
    {
      "name": "Dashboard Overview",
      "components": [
        {
          "type": "stat_card",
          "name": "total_leads",
          "title": "Gesamte Leads",
          "query": "SELECT COUNT(*) as count FROM {{Airtable_Leads.data}}",
          "value": "{{total_leads.data[0].count}}",
          "icon": "users",
          "color": "blue"
        },
        {
          "type": "stat_card",
          "name": "hot_leads",
          "title": "üî• Hei√üe Leads",
          "query": "SELECT COUNT(*) as count FROM {{Airtable_Leads.data}} WHERE score >= 85",
          "value": "{{hot_leads.data[0].count}}",
          "icon": "fire",
          "color": "red"
        },
        {
          "type": "stat_card",
          "name": "conversion_rate",
          "title": "Conversion Rate",
          "query": "SELECT (COUNT(CASE WHEN status = 'Gewonnen' THEN 1 END) * 100.0 / COUNT(*)) as rate FROM {{Airtable_Leads.data}}",
          "value": "{{conversion_rate.data[0].rate}}%",
          "icon": "trending-up",
          "color": "green"
        },
        {
          "type": "stat_card",
          "name": "monthly_revenue",
          "title": "Pipeline Wert",
          "query": "SELECT SUM(potential_revenue) as revenue FROM {{Airtable_Leads.data}} WHERE status IN ('Qualifiziert', 'Angebot', 'Verhandlung')",
          "value": "‚Ç¨{{monthly_revenue.data[0].revenue}}",
          "icon": "euro",
          "color": "yellow"
        },
        {
          "type": "chart",
          "name": "leads_by_status",
          "title": "Leads nach Status",
          "chart_type": "pie",
          "query": "SELECT status, COUNT(*) as count FROM {{Airtable_Leads.data}} GROUP BY status",
          "x_axis": "status",
          "y_axis": "count",
          "colors": {
            "Neu": "#3B82F6",
            "Kontaktiert": "#F59E0B", 
            "Qualifiziert": "#10B981",
            "Angebot": "#8B5CF6",
            "Verhandlung": "#EF4444",
            "Gewonnen": "#059669",
            "Verloren": "#6B7280"
          }
        },
        {
          "type": "chart",
          "name": "leads_by_industry",
          "title": "Leads nach Branche",
          "chart_type": "bar",
          "query": "SELECT industry, COUNT(*) as count, AVG(score) as avg_score FROM {{Airtable_Leads.data}} GROUP BY industry ORDER BY count DESC",
          "x_axis": "industry",
          "y_axis": "count"
        },
        {
          "type": "table",
          "name": "hot_leads_table",
          "title": "üî• Top Priorit√§t Leads",
          "query": "SELECT name, company, industry, score, status, potential_revenue FROM {{Airtable_Leads.data}} WHERE score >= 85 ORDER BY score DESC LIMIT 10",
          "columns": [
            {"key": "name", "title": "Name"},
            {"key": "company", "title": "Unternehmen"},
            {"key": "industry", "title": "Branche"},
            {"key": "score", "title": "Score"},
            {"key": "status", "title": "Status"},
            {"key": "potential_revenue", "title": "Potenzial ‚Ç¨/Monat"}
          ],
          "row_actions": [
            {
              "label": "Kontaktieren",
              "action": "open_url",
              "url": "mailto:{{selectedRow.email}}"
            },
            {
              "label": "Bearbeiten",
              "action": "open_modal",
              "modal": "edit_lead_modal"
            }
          ]
        }
      ]
    },
    {
      "name": "Lead Management",
      "components": [
        {
          "type": "search_filter",
          "name": "lead_search",
          "placeholder": "Leads suchen...",
          "fields": ["name", "company", "industry"]
        },
        {
          "type": "select_filter",
          "name": "status_filter",
          "title": "Status Filter",
          "options": ["Alle", "Neu", "Kontaktiert", "Qualifiziert", "Angebot", "Verhandlung"]
        },
        {
          "type": "select_filter",
          "name": "industry_filter",
          "title": "Branche Filter",
          "options": ["Alle", "Immobilien", "Unternehmensberatung", "Verm√∂gensberatung", "IT-Dienstleister", "Versicherung", "Digitale Agentur"]
        },
        {
          "type": "table",
          "name": "all_leads_table",
          "title": "Alle Leads",
          "query": "SELECT * FROM {{Airtable_Leads.data}} WHERE ({{status_filter.value}} = 'Alle' OR status = {{status_filter.value}}) AND ({{industry_filter.value}} = 'Alle' OR industry = {{industry_filter.value}}) AND (name ILIKE '%{{lead_search.value}}%' OR company ILIKE '%{{lead_search.value}}%') ORDER BY score DESC",
          "pagination": true,
          "page_size": 25,
          "columns": [
            {"key": "name", "title": "Name"},
            {"key": "company", "title": "Unternehmen"},
            {"key": "email", "title": "E-Mail"},
            {"key": "industry", "title": "Branche"},
            {"key": "score", "title": "Score", "format": "number"},
            {"key": "priority", "title": "Priorit√§t"},
            {"key": "status", "title": "Status"},
            {"key": "last_contact", "title": "Letzter Kontakt", "format": "date"},
            {"key": "next_followup", "title": "N√§chster Follow-up", "format": "date"}
          ],
          "row_actions": [
            {
              "label": "E-Mail",
              "action": "open_url",
              "url": "mailto:{{selectedRow.email}}"
            },
            {
              "label": "Anrufen",
              "action": "open_url", 
              "url": "tel:{{selectedRow.phone}}"
            },
            {
              "label": "Bearbeiten",
              "action": "open_modal",
              "modal": "edit_lead_modal"
            }
          ]
        }
      ]
    },
    {
      "name": "Analytics",
      "components": [
        {
          "type": "chart",
          "name": "conversion_funnel",
          "title": "Conversion Funnel",
          "chart_type": "funnel",
          "query": "SELECT status, COUNT(*) as count FROM {{Airtable_Leads.data}} GROUP BY status",
          "stages": [
            {"name": "Neu", "value": "{{conversion_funnel.data.find(d => d.status === 'Neu')?.count || 0}}"},
            {"name": "Kontaktiert", "value": "{{conversion_funnel.data.find(d => d.status === 'Kontaktiert')?.count || 0}}"},
            {"name": "Qualifiziert", "value": "{{conversion_funnel.data.find(d => d.status === 'Qualifiziert')?.count || 0}}"},
            {"name": "Angebot", "value": "{{conversion_funnel.data.find(d => d.status === 'Angebot')?.count || 0}}"},
            {"name": "Gewonnen", "value": "{{conversion_funnel.data.find(d => d.status === 'Gewonnen')?.count || 0}}"}
          ]
        },
        {
          "type": "chart",
          "name": "leads_over_time",
          "title": "Lead-Entwicklung",
          "chart_type": "line",
          "query": "SELECT DATE(created_at) as date, COUNT(*) as count FROM {{Airtable_Leads.data}} WHERE created_at >= DATEADD(CURDATE(), INTERVAL -30 DAY) GROUP BY DATE(created_at) ORDER BY date",
          "x_axis": "date",
          "y_axis": "count"
        },
        {
          "type": "chart",
          "name": "score_distribution",
          "title": "Score-Verteilung",
          "chart_type": "histogram",
